import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './SingleBookPage.css';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { Container } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    root: {
      height: '100vh',
    },
    image: {
      backgroundRepeat: 'no-repeat',
      backgroundColor:
        theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    paper: {
      margin: theme.spacing(8, 4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },

    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(1),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
  }));


export default function SingleBookPage() {

    const classes = useStyles();

    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;

    useEffect( () => {

        const fetchData = async () => {
            try {
                const responseData = await axios.get('http://localhost:8000/api/bookBlog/book/' + bookId);
                const books = responseData.data.book;
                setLoadedBook(books);
            } catch(err) {
                console.log(err.message)
            }
        }
        fetchData();

 }, [bookId, setLoadedBook]);

 return(
     <React.Fragment>
     {loadedBook && 
     <Container maxWidth="md">
    <Grid container component="main" className={classes.root}>
    <CssBaseline />
    <Grid item  sm={4} md={7} className={classes.image}  style={{ backgroundImage: `url(${loadedBook.imageUrl})` }}  />

    <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>


      <div className={classes.paper}>
        <Typography component="h1" variant="h5">
          {loadedBook.title}
        </Typography>
        <Typography component="h2" variant="subtitle1">
          {loadedBook.author}
        </Typography>
        <Typography component="h2" variant="subtitle1">
          {loadedBook.description}
        </Typography>
        <Typography component="h2" variant="subtitle1">
          {loadedBook.pages}
        </Typography>




          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="#" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>

      </div>
      </Grid>
        </Grid>
  </Container>

     }
     </React.Fragment>
);

}
