import React, { useContext } from 'react';
import './SearchResultsPage.css'
import { Container, Card, CardContent, Typography, CardActions, Button, CardMedia } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      marginBottom: '30px',
      position: 'relative'
    },
    details: {
      display: 'flex',
      flexDirection: 'column',
    },
    content: {
      flex: '1 0 auto',
    },
    cover: {
      width: 151,
    },

    description: {
        margin: '10px'
    }

  }));


export default function SearchResultsPage(props) {

    const classes = useStyles();
    const history = useHistory();
    const auth = useContext(AuthContext);
    const results = props.location.state.results;



    const addToList = id => event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(response => {
            console.log(response.data);
            history.push('/booklist/' + auth.userId);  
        })
        .catch(err => console.log(err));    
    }

    return (
        <React.Fragment>
        {results ?
        <Container>
         <Card className={classes.root}>
              <CardMedia
                    className={classes.cover}
                    image={results.imageUrl}
                    title={results.title}
                />
            <div className={classes.details}>
                <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                       <Link to={/book/ + results.id }> {results.title} ({results.year}) </Link>
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {results.author}
                    </Typography>
                    <hr />
                    <Typography align="justify" className={classes.description} variant="body2" component="p">
                        {results.description}
                    </Typography>
                    <br />
                    <div>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {results.page} </strong>  
                    </Typography>
                    <CardActions className="cardActions">
                       {auth.isLoggedIn && auth.role === 1 &&
                        <Button
                          type="submit"
                          onClick={addToList(id)}
                          className="bookItemButton"
                          variant="outlined"
                          color="primary"
                          size="small">add to bookList
                        </Button>}
                    </CardActions>
                    </div>
                    </CardContent>
                    </div>


        </Card>
    </Container>: null }
    </React.Fragment>
    )
}
