import React, { useState, useEffect, useRef, useMemo } from 'react';
import axios from 'axios';
import { Redirect, withRouter, useParams } from 'react-router-dom';

const Search = React.memo(props => {

    //const [searchText, setSearchText] = React.useState('');
    const [searchResult, setSearchResult] = React.useState([]);
    const [redirect, setRedirect] = useState(false);

    const title = useRef('');
    const userId = useParams().userId
    const handleKeyPress = (event) => {
        title.current = event.target.value;
        if(event.key === "Enter") {
            axios.get('http://localhost:8000/api/bookBlog/book/title/' + userId + '/' + title.current) 
            .then( response => {
                const book = response.data.book;
                setSearchResult(book);
                setRedirect(true)
            })
            .catch(err => console.log(err))       

        }
    }


  return (
      <React.Fragment>
           <div>
            <input
                type="text"
                placeholder="Search"
                onKeyPress={handleKeyPress}
        />
         {redirect &&
         <Redirect to={{
            pathname: '/results/:userId',
            search: '?query=' + title.current,
            state: { results: searchResult }
         }} /> }

      </div>


                </React.Fragment>

  );
});


export default withRouter(Search);
