import React, { useEffect, useState } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from 'axios';
import { Button } from '@material-ui/core';
import './AdminPanel.css'

export default function AdminPanel() {


    const [loadedBooks, setLoadedBooks] = useState([])


    useEffect( () => {

        axios.get('http://localhost:8000/api/bookBlog/books/')
        .then( response => {
            const books = response.data.books;
            const bookArray = []
            for(const key in books) {
              bookArray.push({
                id: key,
                title: books[key].title,
                author: books[key].author,
                imageUrl: books[key].imageUrl,
                description: books[key].description,
                year: books[key].year,
                pages: books[key].pages
              })

            } 
            setLoadedBooks(bookArray)   

        })
        .catch(err => console.log(err))       
 }, []);

    return (

        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell align="left">Title </TableCell>
                <TableCell align="left">Author </TableCell>
                <TableCell align="left"> ImageURL </TableCell>
                <TableCell align="left"> Description</TableCell>
                <TableCell align="left"> Year</TableCell>
                <TableCell align="left"> Pages</TableCell>
                <TableCell align="left">Actions</TableCell>

              </TableRow>
            </TableHead>
            <TableBody>
              { loadedBooks && loadedBooks.map((row) => (
                <TableRow key={row.id}>
                  <TableCell component="th" scope="row">
                    {row.title}
                  </TableCell>
                  <TableCell align="right"><div> {row.author} </div></TableCell>
                  <TableCell align="right" className="text"> <div className="text">{row.imageUrl}</div></TableCell>
                  <TableCell align="right" ><div className="text2">{row.description}</div></TableCell>
                  <TableCell align="right">{row.year}</TableCell>
                  <TableCell align="right">{row.pages}</TableCell>
                  <TableCell align="right"><Button>EDIT</Button></TableCell>


                </TableRow>
              )) }
            </TableBody>
          </Table>
        </TableContainer>
      );


}
