import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import './UserBookList.css';
import UserBooklistItem from '../userBooklistItem/UserBooklistItem';

export default function UserBookList() {

    const [loadedBook, setLoadedBook] = useState([]);
    const [bookIndex, setBookIndex] = useState()

    const userId = useParams().userId;

    const [open, setOpen] = useState(false);

    const handleclickOpen = (index) => {
        let bookId = index;
         setBookIndex(bookId);
         setOpen(true);
       };

       const handleClose = () => {
         setOpen(false);
       }

    useEffect( () => {

        const getUseBooks = async () => {

                 await axios.get('http://localhost:8000/api/bookBlog/booklist/' + userId )
                .then( books =>{
                    let userBooks = books.data.books;
                    setLoadedBook(userBooks);
                })
                .catch(err => console.log(err))

        }
        return getUseBooks();    

    }, [userId]);



    const handleDeleteUserBook = (id) =>{ 
        id = bookIndex;
        setOpen(false)
         fetch('http://localhost:8000/api/bookBlog/booklist/delete/' + userId,
         {
             method: "DELETE",
            headers:{'Content-Type': 'application/json'} ,
            body: JSON.stringify({id: id})
         })
        .then( response => {
           return response.json()

        })
        .then((deletedBook) => {
            let newArray = loadedBook.filter(book => book.id !== deletedBook.id);
            setLoadedBook(newArray)
        })
        .catch(err => console.log(err))       
    }

    return(
        <Container className="container">
        <h3>Your Booklist</h3>

        <ul className="userBookList">
        {loadedBook.length === 0 ?
            <p className="listIsEmpty"> Your list is empty. Maybe add some book?</p>
             :
            loadedBook &&  loadedBook.map(book => {
                return <UserBooklistItem
                    key = {book.id}
                    id = {book.id}
                    title={book.title}
                    author={book.author}
                    imageUrl= {book.imageUrl}
                    open={open}
                    handleclickOpen={handleclickOpen}
                    handleClose={handleClose}
                    bookIndex={bookIndex}
                    handleDeleteUserBook= {handleDeleteUserBook}
             />})
        }
        </ul>
        </Container>
    )
}
