import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './SingleBookPage.css';


export default function SingleBookPage() {

    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;
    useEffect( () => {

        const fetchData = async () => {
            try {
                const responseData = await axios.get('http://localhost:8000/api/bookBlog/book/' + bookId);
                const books = responseData.data.book;
                setLoadedBook(books);
            } catch(err) {
                console.log(err.message)
            }
        }
        fetchData();

 }, [bookId, setLoadedBook]);

 return(
    <div className="singleBookContainer">
        {loadedBook && 
        <React.Fragment>
            <div className="mainContent">
                <div className="mainContentFloat">
                    <div className="leftContainer">
                        <div className="mainCol">
                        <div className="imageDiv">
                            <img src={loadedBook.imageUrl} alt={loadedBook.title} />
                        </div>
                        <div className="bookContent">
                            <h2 className="title" > {loadedBook.title} </h2>
                            <h3 className="author"> by {loadedBook.author} </h3>
                            <article className="description">
                                {loadedBook.description}
                            </article>
                         </div>
                         </div>
                            </div>
                        </div>
                        </div>
        </React.Fragment>
        }
    </div>
)
}
