import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { Card, CardContent, Typography, CardActions, Button } from '@material-ui/core';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import './Card.css';
import ReactFlipCard from 'react-card-flip';


export default function BookCard(props) {

    const auth = useContext(AuthContext);
    const history = useHistory();
    let userData = JSON.parse(localStorage.getItem('userData'));
    let userBookList = userData.userBookList;

    const [isFlipped, setIsFlipped] = useState(false);

    const handleFlip = () => {
        setIsFlipped(!isFlipped);
    }

    const addToList = id => event => {

        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(responseData => {
            if(responseData) {
                const addedBookId = responseData.data.id;
                userBookList.push(addedBookId);
                localStorage.setItem('userData', JSON.stringify(userData))
                history.push('/' + auth.userId + '/booklist/');  

            }
        })
        .catch(error => {
            if (error.response) {
                console.log(error.response.data);
                console.log(error.response.status);
                console.log(error.response.headers);
              }
        });    
    }



    return(
        <ReactFlipCard isFlipped={isFlipped} >
        <div className="bookItem_root"  onClick={handleFlip}>
              <div className="imageDiv">
                  <img src={props.imageUrl}></img>
              </div>

        </div>
        <div  onClick={handleFlip}>
                <CardContent className="content">
                <div>

                    <Typography component="h6" variant="h6">
                       <Link to={/book/ + props.id }> {props.title} ({props.year}) </Link>
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {props.author}
                    </Typography>
                    <hr />
                    <Typography align="justify" className="description" variant="body2" component="p">
                        {props.description}
                    </Typography>
                    </div>
                    <div>
                      <footer>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {props.pages} </strong>  
                    </Typography>
                    <div className="spacer"></div>
                    <CardActions style={{marginTop: "9px"}} className="cardActions">
                       {auth.isLoggedIn && auth.role === 1  &&
                      userBookList.includes(props.id) ?
                        <Button
                          type="submit"
                          variant="outlined"
                          disabled={true}
                          color="primary"
                          size="small">
                           book is added
                        </Button> :  <Button
                          type="submit"
                          onClick={addToList(props.id)}
                          variant="outlined"
                          color="primary"
                          size="small">
                           Add book to list
                        </Button>

}
                    </CardActions>
                    </footer>
                    </div>
                    </CardContent>


        </div>
        </ReactFlipCard>
    )
}
