import React, { useContext } from 'react';
import axios from 'axios';
import { useHistory, Link } from 'react-router-dom';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import './BookItem.css'
import { Button } from '@material-ui/core';
import { AuthContext } from '../../context/AuthContext';
import BookCard from '../../shared/Card/Card';



export default function BookItem({id, title, description, author, year, imageUrl, pages}) {

    const history = useHistory();
    const auth = useContext(AuthContext);


    const addToList = id => event => {
        event.preventDefault();
        axios.post(`http://localhost:8000/api/bookBlog/booklist/${auth.userId}`, {
           id: id,
           userId: auth.userId
        })
        .then(response => {
            console.log(response.data);
            history.push(`/${auth.userId}/booklist`  );  
        })
        .catch(err => console.log(err));    
    }

    return(
       <BookCard
        addToList={addToList}
        id={id}
        title={title}
        imageUrl={imageUrl}
        author={author}
        description={description}
        year={year}
        pages={pages}
         />
    )
}
