import React, { useContext, useCallback, useEffect, useState } from 'react';
import AppHeader from './navigation/header/Header';
import {BrowserRouter as Router, Route, Switch, Redirect} from 'react-router-dom';
import { StylesProvider } from '@material-ui/core';
import AddBook from './Admin/addBook/AddBook';
import BookList from './Books/userBooksList/UserBookList';
import Signup from './auth/signup/Signup';
import Login from './auth/login/Login';
import GeneralBookList from './Books/GeneralBooklist/GeneralBooklist';
import SingleBookPage from './Books/SingleBookPage/SingleBookPage';
import { AuthContext } from './context/AuthContext';
import Home from './home';
import AdminPanel from './Admin/adminPage/AdminPanel';
import AdminTable from './Admin/AdminTable/AdminTable';
import EditBook from './Admin/editBook/EditBook';






function App() {

  const [userId, setUserId] = useState(false);
  const [token, setToken] = useState(false);


  const login = useCallback((uid, utoken) => {
    setUserId(uid);
    setUserId(utoken);
    localStorage.setItem(
      'userData',
      JSON.stringify({
         userId: uid,
         token: utoken
      })
    )
  }, []);


  const logout = useCallback(() => {
    setUserId(null);
    setToken(null);
    localStorage.removeItem('userData');
  }, []); 



  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('userData'));
    if(storedData) {
      login(storedData.userId, storedData.token);
    }

  },[login]);



  let routes;

  if(token) {
    routes = (
      <Switch>
        <Route path="/" exact component={Home} />
        <Route path="/books/new" component={AddBook}  />
        <Route path="/booklist/:userId" component={BookList} />
        <Route path="/books/" component={GeneralBookList} />
        <Route path="/book/:bookId" component={SingleBookPage} />
        <Route path="/admin/edit/:bookId" component={EditBook} />

        <Redirect to="/books" />
      </Switch>
    )} else {
     routes = (
       <Switch>
        <Route path="/" exact component={Home} />
        <Route path="/admin/adminpanel" exact component={AdminPanel} />
        <Route path="/books/new" component={AddBook}  />
        <Route path="/admin/admintable" component={AdminTable} />
        <Route path="/admin/edit/:bookId" component={EditBook} />
        <Route path="/books/" component={GeneralBookList} />
        <Route path="/user/signup" component={Signup} /> 
        <Route path="/user/login" component={Login} />
        <Redirect to="/user/login" />
      </Switch>
     )
   };

  return (

    <StylesProvider injectFirst>
      <AuthContext.Provider value={{
      isLoggedIn: !!token,
      token:token,
      userId: userId,
      login: login,
      logout: logout
    }}>
      <Router>
        <AppHeader />

        <main>

        {routes}
        </main>
      </Router>
      </AuthContext.Provider>
    </StylesProvider>
  );
}

export default App;
