import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { Card, CardContent, Typography, CardActions, Button } from '@material-ui/core';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import './Card.css';



export default function BookCard(props) {

    const auth = useContext(AuthContext);
    const history = useHistory();

    const [error, setError] = useState('');
    const [bookIsAdded, setBookIsAdded] = useState(false);

    const addToList = id => event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(response => {
            if(response) {}
            setBookIsAdded(true)
            history.push('/' + auth.userId + '/booklist/');  
        })
        .catch(err => {
            setBookIsAdded(false)
            return (setError(err.response.data.message));
        });    
    }
    {console.log(error)}
    return(

        <Card className="bookItem_root">
              <div className="imageDiv">
                  <img src={props.imageUrl}></img>
              </div>
                <CardContent className="content">
                <div>

                    <Typography component="h5" variant="h5">
                       <Link to={/book/ + props.id }> {props.title} ({props.year}) </Link>
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {props.author}
                    </Typography>
                    <hr />
                    <Typography align="justify" className="description" variant="body2" component="p">
                        {props.description}
                    </Typography>
                    </div>
                    <div>
                      <footer>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {props.pages} </strong>  
                    </Typography>
                    <div className="spacer"></div>
                    <CardActions className="cardActions">
                       {auth.isLoggedIn && auth.role === 1 &&
                       !bookIsAdded ?
                        <Button
                          type="submit"
                          onClick={addToList(props.id)}
                          variant="outlined"
                          color="primary"
                          size="small">
                              add to bookList
                        </Button>: 
                        <Button
                            color="primary"
                            disabled="true">
                                Already added
                        </Button>}
                    </CardActions>
                    </footer>
                    </div>
                    </CardContent>


        </Card>
    )
}
