import React from 'react';
import { Button, Card, makeStyles, CardContent, CardActionArea } from '@material-ui/core';
import DeleteUSerBookDialog from '../DeleteModel/Deletemodel';
import './UserBooklistItem.css';

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });


export default function UserBooklistItem({
     id,
     imageUrl,
     title,
     author,
     open,
     bookIndex,
     handleclickOpen,
     handleClose,
     handleDeleteUserBook
    }) {    
    const classes = useStyles();

    return(
            <div className="container">
                <div className="card-wrap">
                <Card className={classes.root}>
                    <CardActionArea>
                    <div className="cardImg">
                        <img src={imageUrl} />
                    </div>
                    <CardContent className="card-content flexible">
                        <h3 className="card-title"> {title} </h3>
                        <h4 className="card-author"> {author} </h4>
                        <Button className="removeBookButton" onClick={(e) => handleclickOpen(id)} color="primary"> remove book</Button>
                        <DeleteUSerBookDialog
                           open={open}
                           handleClose={handleClose}
                           handleclickOpen={handleclickOpen}
                           handleDeleteUserBook={handleDeleteUserBook}
                           bookid={bookIndex}/>
                    </CardContent>
                    </CardActionArea>
                </Card>
                </div>
            </div>
    )

}
