import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Grid, Paper, GridList, GridListTile, ListSubheader } from '@material-ui/core';
import BookCard from '../../shared/Card/Card';
import './GeneralBooklist.css';

export default function GeneralBookList() {

    const [loadedBook, setLoadedBook] = useState([]);

    useEffect( () => {

           axios.get('http://localhost:8000/api/bookBlog/books/')
           .then( response => {
               const books = response.data.books;
               setLoadedBook(books)
           })
           .catch(err => console.log(err))       
    }, []);


    return(

            <div className="booklist_root">
                <h1> All Books</h1>
                    <GridList cellHeight={280} className="gridList">
                    <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>

        </GridListTile>


        {loadedBook &&  loadedBook.map(book => {
            return <li> <BookCard
             key = {book.id}
             id = {book.id}
             title={book.title}
             author={book.author}
             pages={book.pages}

             description={book.description}
             imageUrl= {book.imageUrl}
             year={book.year}
             /></li>})
        }


        </GridList>

        </div>   
    )
}
