import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container } from '@material-ui/core';
import './SingleBookPage.css';


export default function SingleBookPage() {

    const [loadedBook, setLoadedBook] = useState([]);


    const bookId = useParams().bookId
    useEffect( () => {

        axios.get('http://localhost:8000/api/bookBlog/book/' + bookId)
        .then( response => {
            const books = response.data.book;

            setLoadedBook(books)
        })
        .catch(err => console.log(err))       
 }, [bookId, setLoadedBook]);

 return(
    <div className="singleBookContainer">
        {loadedBook && 
        <React.Fragment>
            <div className="mainContent">
                <div className="mainContentFloat">
                    <div className="leftContainer">
                        <div className="mainCol">
                        <div className="imageDiv">
                            <img src={loadedBook.imageUrl} />
                        </div>
                        <div className="bookContent">
                            <h1> {loadedBook.title} </h1>
                            <p className="author"> by {loadedBook.author} </p>
                         </div>
                         </div>
                            </div>
                        </div>
                        </div>


        </React.Fragment>
        }
    </div>
)
}
