import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Container } from '@material-ui/core';
import BookItem from '../BookItem/BookItem';
import './BookList.css';

export default function BookList() {

    const [loadedBook, setLoadedBook] = useState([]);



    useEffect( () => {

           axios.get('http://localhost:8000/api/bookBlog/books')
           .then( response => {
               const books = response.data.books;
               setLoadedBook(books)
               console.log(books)
           })
           .catch(err => console.log(err))       
    }, []);

    const addToList = id => event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist', {
           id: id
        })
        .then(res => {
            console.log(res.data);

            //history.push('/books');

          })
          .catch(err => console.log(err))

    }


    return(
        <Container>
        <h1>Your BookList</h1>
        <ul>
        {loadedBook &&  loadedBook.map(book => {
            return <BookItem
             key = {book.id}
             id = {book.id}
             title={book.title}
             author={book.author}
             pages={book.pages}

             description={book.description}
             imageUrl= {book.imageUrl}
             year={book.year}
             addToList={addToList}
             />})
        }
        </ul>
        </Container>
    )
}
