import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { Container } from '@material-ui/core';
import './SingleBookPage.css'

export default function SingleBookPage() {


    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;

    useEffect( () => {

        const fetchData = async () => {
            try {
                const responseData = await axios.get('http://localhost:8000/api/bookBlog/book/' + bookId);
                const book = responseData.data;
                setLoadedBook(book);
            } catch(err) {
                console.log(err.message)
            }
        }
        fetchData();

 }, [bookId, setLoadedBook]);

 return(
     <React.Fragment>
      {loadedBook && 
       <div className="book-bg">
               <Container className="paper">
                  <Typography component="h1" variant="h5">
                    {loadedBook.title}
                  </Typography>
                  <Typography component="h2" variant="subtitle1">
                    by {loadedBook.author}
                  </Typography>
                  <Typography className="book_desc" component="h2" variant="subtitle1">
                    {loadedBook.description}
                  </Typography>
                  <footer>
                  <Typography component="h2" variant="subtitle1">
                    Pages: {loadedBook.pages}
                  </Typography>
                </footer>
               </Container>
        <div className="book-cover" style={{ backgroundImage: `url(${loadedBook.imageUrl})` }}>

        </div>
      </div>}

    </React.Fragment>
  );

}
