import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { Container } from '@material-ui/core';
import './SingleBookPage.css'



export default function SingleBookPage() {


    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;

    useEffect( () => {

        const fetchData = async () => {
            try {
                const responseData = await axios.get('http://localhost:8000/api/bookBlog/book/' + bookId);
                const book = responseData.data;
                setLoadedBook(book);
            } catch(err) {
                console.log(err.message)
            }
        }
        fetchData();

 }, [bookId, setLoadedBook]);

 return(
     <React.Fragment>
      {loadedBook && 
        <Container className="book_container">
          <Grid container component="main" className="singleBookPageRoot">
            <CssBaseline />
            <Grid item  sm={4} md={5} className="image" style={{ backgroundImage: `url(${loadedBook.imageUrl})` }}  />
              <div className="aside">
                <h3>{loadedBook.title} </h3>
              </div>
            <Grid item  sm={8} md={5} component={Paper} elevation={6} square>  
               <div className="paper">
                  <Typography component="h1" variant="h5">
                    {loadedBook.title}
                  </Typography>
                  <Typography component="h2" variant="subtitle1">
                    by {loadedBook.author}
                  </Typography>
                  <Typography className="book_desc" component="h2" variant="subtitle1">
                    {loadedBook.description}
                  </Typography>
                  <footer>
                  <Typography component="h2" variant="subtitle1">
                    Pages: {loadedBook.pages}
                  </Typography>
                </footer>
               </div>
          </Grid>
        </Grid>
      </Container>}
    </React.Fragment>
  );

}
