import React, { useEffect, useState, useCallback, } from 'react';
import './AdminPanel.css'
import axios from 'axios';
import AdminTable from '../AdminTable/AdminTable';
import DeleteBookDialog from '../deleteBook/DeleteBookModal';
import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';

export default function AdminPanel() {

    const [loadedBooks, setLoadedBooks] = useState([]);
    const [bookIndex, setBookIndex] = useState();
    const [open, setOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState("");

    const handleclickOpen = (index) => {
     let bookId = index;
      setBookIndex(bookId);
      setOpen(true);
    };

    const handleClose = () => {
      setOpen(false);
    }

    useEffect( () => {

      const fetchBooks = async () => {
        try {
          const responseData = await axios.get('http://localhost:8000/api/bookBlog/books/');
          const books = responseData.data;
          const bookArray = []
          for(const key in books) {
            bookArray.push({
              id: key,
              bookId: books[key].id,
              title: books[key].title,
              author: books[key].author,
              imageUrl: books[key].imageUrl,
              description: books[key].description,
              year: books[key].year,
              pages: books[key].pages
            })
          } 
          setLoadedBooks(bookArray)   

        }
        catch(err) {
          setError(err.response.data.message)
        }
      }

        fetchBooks();

    }, []);

      const handleDeleteBook = useCallback(() => {
        setOpen(false);
        axios.delete("http://localhost:8000/api/admin/books/delete/" + bookIndex)
        .then(response => {
          setLoadedBooks(prevBooks => prevBooks.filter(book => book.bookId !== bookIndex))
        })
        .catch(err => console.log(err));
      }, [bookIndex]);


    return (
        <React.Fragment>
            <DeleteBookDialog 
              open={open}
              handleDeleteBook={handleDeleteBook}
              handleClose={handleClose}
              bookid={bookIndex} />
            {loadedBooks && !error &&  isLoading && <CircularIndeterminate size="7rem" color="primary" />}
            <AdminTable
             loadedBooks={loadedBooks}
             handleclickOpen={handleclickOpen}
              /> }
        </React.Fragment>
      );

}
