import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { Card, CardContent, Typography, CardActions, Button } from '@material-ui/core';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import './Card.css';



export default function BookCard(props) {

    const auth = useContext(AuthContext);
    const history = useHistory();

    const [state, setState ] = useState({disabled: true, label: "add"})

    const addToList = id => event => {
        setState({disabled: !state.disabled, label: 'Save'});

        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(responseData => {
            const booklist = responseData.data.id;
            var persons = JSON.parse(localStorage.getItem('userData'));
            console.log(persons.userBookList)


            history.push('/' + auth.userId + '/booklist/');  
        })
        .catch(error => {
            if (error.response) {
                console.log(error.response.data);
                console.log(error.response.status);
                console.log(error.response.headers);
              }
        });    
    }
    return(

        <Card className="bookItem_root">
              <div className="imageDiv">
                  <img src={props.imageUrl}></img>
              </div>
                <CardContent className="content">
                <div>

                    <Typography component="h5" variant="h5">
                       <Link to={/book/ + props.id }> {props.title} ({props.year}) </Link>
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {props.author}
                    </Typography>
                    <hr />
                    <Typography align="justify" className="description" variant="body2" component="p">
                        {props.description}
                    </Typography>
                    </div>
                    <div>
                      <footer>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {props.pages} </strong>  
                    </Typography>
                    <div className="spacer"></div>
                    <CardActions className="cardActions">
                       {auth.isLoggedIn && auth.role === 1 &&
                        <Button
                          type="submit"
                          onClick={addToList(props.id)}
                          variant="outlined"
                          color="primary"
                          size="small">
                             {state.label}
                        </Button>
}
                    </CardActions>
                    </footer>
                    </div>
                    </CardContent>


        </Card>
    )
}
