import React from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button, TableFooter, TablePagination } from '@material-ui/core';
import MyTablePaginationActions from '../Pagination/MyTablePaginationActions';


export default function AdminTable({
    handleclickOpen,
    loadedBooks,
    handleChangePage,
    handleChangeRowsPerPage,
    page, 
    rowsPerPage
  }) {

function createData(title, author, description, imageUrl) {
    return {title, author, description, imageUrl  };
}

const rows = loadedBooks && loadedBooks.map(book => {
  createData(book.title, book.author, book.description, book.imageUrl)
})

console.log(rows)


  const emptyRows = rowsPerPage - Math.min(rowsPerPage, loadedBooks.length - page * rowsPerPage);



    return(
        <TableContainer component={Paper}>
            <h2>Books</h2>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell align="left">Title </TableCell>
                <TableCell align="left">Author </TableCell>
                <TableCell align="left"> ImageURL </TableCell>
                <TableCell align="left"> Description</TableCell>
                <TableCell align="left"> Year</TableCell>
                <TableCell align="left"> Pages</TableCell>
                <TableCell align="left"className="actions">Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              { loadedBooks &&
              (rowsPerPage > 0
                ? loadedBooks.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                : loadedBooks
              ).map((row) => (
                <TableRow key={row.id}>
                  <TableCell  name="title" component="th" scope="row">{row.title}</TableCell>
                  <TableCell  name="author" align="right"><div> {row.author} </div></TableCell>
                  <TableCell  name="imageUrl" align="right">{row.imageUrl}</TableCell>
                  <TableCell  name="description" align="right" className="text2">{row.description}</TableCell>
                  <TableCell  name="year" align="right">{row.year}</TableCell>
                  <TableCell  name="pages" align="right">{row.pages}</TableCell>
                  <TableCell align="right">
                    <Button href={"/admin/edit/" + row.bookId} >EDIT</Button>
                    <Button color="secondary"  onClick={(e) => handleclickOpen(row.bookId)} >DELETE</Button>
                  </TableCell>
                </TableRow>
              )) }
                {emptyRows > 0 && (
            <TableRow style={{ height: 53 * emptyRows }}>
              <TableCell colSpan={6} />
            </TableRow>
          )}
            </TableBody>
            <TableFooter>
                <TablePagination
                rowsPerPageOptions={[3, 6, 25, { label: 'All', value: -1 }]}
                count={loadedBooks.length}
                rowsPerPage={rowsPerPage}
                page={page}
                SelectProps={{
                  inputProps: { 'aria-label': 'rows per page' },
                  native: true,
                }}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
                ActionsComponent={MyTablePaginationActions}
                 />
            </TableFooter>
          </Table>
        </TableContainer>
    )
}
