import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import UserBookListItem from './userBookListItem/UserBookListItem'
import './UserBookList.css';

export default function UserBookList() {

    const [loadedBook, setLoadedBook] = useState([]);
    const userId = useParams().userId;

    useEffect( () => {

           axios.get('http://localhost:8000/api/bookBlog/booklist/' + userId)
           .then( response => {
               const books = response.data.books;
               setLoadedBook(books)
           })
           .catch(err => console.log(err))       
    }, [userId]);



    return(
        <Container className="container">
        <h1>Your Booklist</h1>

        <ul>
        {loadedBook.length === 0 ?
            <p className="listIsEmpty"> Your list is empty. Maybe add some book?</p>
             :
            loadedBook &&  loadedBook.map(book => {
                return <UserBookListItem
                    key = {book.id}
                    id = {book.id}
                    title={book.title}
                    author={book.author}
                    pages={book.pages}
                    description={book.description}
                    imageUrl= {book.imageUrl}
                    year={book.year}
             />})
        }
        </ul>
        </Container>
    )
}
