import React from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button, TableFooter, TablePagination } from '@material-ui/core';
import MyTablePaginationActions from '../Pagination/MyTablePaginationActions';


export default function AdminTable({
    handleclickOpen,
    loadedBooks,
    handleChangePage,
    handleChangeRowsPerPage,
    page, 
    rowsPerPage
  }) {

    const columns = [
      {
        id: 'title',
        label: 'title',
        align: 'right',
        format: (value) => value.toLocaleString('en-US'),
      },
      {
        id: 'author',
        label: 'Author',
        align: 'right',
        format: (value) => value.toLocaleString('en-US'),
      },
      {
        id: 'imageUrl',
        label: 'imageUrl',
        align: 'right',
        format: (value) => value.toFixed(2),
      },
      {
        id: 'description',
        label: 'Description',
        align: 'right',
        format: (value) => value.toFixed(2),
      },
      {
        id: 'year',
        label: 'Year',
        align: 'right',
        format: (value) => value.toFixed(2),
      },
      {
        id: 'pages',
        label: 'Pages',
        align: 'right',
        format: (value) => value.toFixed(2),
      },
      {
        id: 'actions',
        label: 'Actions',
        align: 'right',
        format: (value) => value.toFixed(2),
      },
    ];

function createData(bookId, title, author, imageUrl, description, year, pages) {
    return {bookId, title, author, imageUrl, description, year, pages  };
}


const rows =  loadedBooks.map(book => {
  return createData(
      book.bookId,
      book.title,
      book.author,
      book.imageUrl,
      book.description,
      book.year,
      book.pages)
})

const emptyRows = rowsPerPage - Math.min(rowsPerPage, loadedBooks.length - page * rowsPerPage);



    return(
        <TableContainer component={Paper}>
            <h2>Books</h2>
          <Table>
            <TableHead>
              <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
              </TableRow>
            </TableHead>
            <TableBody>
              { loadedBooks &&
              (rowsPerPage > 0
                ? rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                : rows
              ).map((row) => (
                <TableRow key={row.id}>
                  <TableCell  name="title" component="th" scope="row">{row.title}</TableCell>
                  <TableCell  name="author" align="right"><div> {row.author} </div></TableCell>
                  <TableCell  name="imageUrl" align="right">{row.imageUrl}</TableCell>
                  <TableCell  name="description" align="right" className="text2">{row.description}</TableCell>
                  <TableCell  name="year" align="right">{row.year}</TableCell>
                  <TableCell  name="pages" align="right">{row.pages}</TableCell>
                  <TableCell align="right">
                    <Button href={"/admin/edit/" + row.bookId} >EDIT</Button>
                    <Button color="secondary"  onClick={(e) => handleclickOpen(row.bookId)} >DELETE</Button>
                  </TableCell>
                </TableRow>
              )) }
            </TableBody>
            <TableFooter>
                <TablePagination
                rowsPerPageOptions={[3, 6, 25]}
                count={rows.length}
                rowsPerPage={rowsPerPage}
                page={page}
                SelectProps={{
                  inputProps: { 'aria-label': 'rows per page' },
                  native: true,
                }}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
                ActionsComponent={MyTablePaginationActions}
                 />
            </TableFooter>
          </Table>
        </TableContainer>
    )
}
