import React, { useEffect, useState } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';
import './AdminPanel.css'
import EditBook from '../editBook.js/EditBook';
import axios from 'axios';
import {useParams, useHistory} from 'react-router-dom'

export default function AdminPanel() {


    const [loadedBooks, setLoadedBooks] = useState([])
    const [values, setValues] = useState();
    const history = useHistory();

    const bookId = useParams().bookId;


    const handleSubmit = (event) => {
        event.preventDefault();
        const newbook = {
          title: values.title,
          author: values.author,
          imageUrl: values.imageUrl,
          year: values.year,
          pages: values.pages,
          description: values.description
        } 
        setValues(newbook);

        axios.patch (`http://localhost:8000/api/admin/books/edit/` + bookId, newbook)
        .then(res => {
          console.log(res.data);
          history.push('/admin/edit/' + bookId);

        })
        .catch(err => console.log(err))
    }; 



    useEffect( () => {

        axios.get('http://localhost:8000/api/bookBlog/books/')
        .then( response => {
            const books = response.data.books;
            const bookArray = []
            for(const key in books) {
              bookArray.push({
                id: key,
                bookId: books[key].id,
                title: books[key].title,
                author: books[key].author,
                imageUrl: books[key].imageUrl,
                description: books[key].description,
                year: books[key].year,
                pages: books[key].pages
              })

            } 
            setLoadedBooks(bookArray)   

        })
        .catch(err => console.log(err))       
 }, []);

 const handleChange = (event) => {
    event.persist();
    setValues(values => ({
      ...values,
      [event.target.name]: event.target.value
  }));
 }

    return (
        <React.Fragment>
            <AdminPanel handleChange={handleChange} handleSubmit={handleSubmit} />
            <EditBook handleChange={handleChange} handleSubmit={handleSubmit}  />
        </React.Fragment>
      );


}
