import React, { useContext } from 'react';
import './SearchResultsPage.css'
import { Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import BookCard from '../../shared/Card/Card';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      marginBottom: '30px',
      position: 'relative'
    },
    details: {
      display: 'flex',
      flexDirection: 'column',
    },
    content: {
      flex: '1 0 auto',
    },
    cover: {
      width: 151,
    },

    description: {
        margin: '10px'
    }

  }));


export default function SearchResultsPage(props) {

    const history = useHistory();
    const auth = useContext(AuthContext);
    const results = props.location.state.results;



    const addToList = id => event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(response => {
            console.log(response.data);
            history.push('/booklist/' + auth.userId);  
        })
        .catch(err => console.log(err));    
    }

    return (
        <React.Fragment>
        {results ?
        <Container>
         <BookCard
          addToList={addToList}
          id={results.id}
          title={results.title}
          author = {results.author}
          imageUrl={results.imageUrl}
          year ={results.year}
          pages = {results.pages}

           />
    </Container>: null }
    </React.Fragment>
    )
}
