import React from 'react';
import { TextField, Button } from '@material-ui/core';
import { useParams } from 'react-router-dom';


export default function EditBook() {

    const bookId = useParams().bookId

    useEffect(() => {
        const fetchBOOK = async () => {
          try {
            const responseData = await sendRequest(
              `http://localhost:8000/api/bookBlog/book/${bookId}`
            );
            setLoadedPlace(responseData.place);
            setFormData(
              {
                name: {
                  value: responseData.place.name,
                  isValid: true
                },
                description: {
                  value: responseData.place.description,
                  isValid: true
                }
              },
              true
            );

          } catch (err) {
            console.log(err.message)
          }
        };
        fetchPlace();
      }, [sendRequest, placeId, setFormData]);

    return(
        <form  className="bookForm" >
        <h2>Edit Book</h2>

        <TextField 
        id="input"
        label="title"
        type="text"
        name="title"
        required
        />
        <TextField
        id="input"
        label="author"
        type="text"
        name="author"
        required
         />
        <TextField
        id="input"
        label="imageUrl"
        type="text"
        name="imageUrl"
        required
         />
        <TextField
        id="input"
        label="year"
        type="number"
        name="year"
        required
        />
        <TextField
        id="input"
        label="pages"
        type="number"
        name="pages"
        required
         />
        <TextField
        id="textarea"
        label="description"
        type="text"
        name="description"
        multiline
        rows={3}
        rowsMax={3}
        />

      <Button
        className="editBookButton"
        color="primary"
        size="large"
        variant="contained"
        type="submit" >
             ADD BOOK
        </Button> 

    </form>
    )
}
