import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import './Header.css';
import SearchInput from '../searchInput/SearchInput';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import SelectRole from './selectRole/SelectRole';
import userNavLinks from '../header/Navlinks/userNavLinks';
import AdminLinks from '../header/Navlinks/adminNavLinks';
import UserNavLinks from '../header/Navlinks/userNavLinks';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  }
}))

export default function TereTere()  {

    const auth = useContext(AuthContext);
    const classes = useStyles();
    return(
        <React.Fragment>
            <AppBar position="static">
                <Toolbar>
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton>
                    <h2>BookReader</h2>
                    <SearchInput />
                    <div className="spacer"></div>
                    { !auth.isLoggedIn &&       
                    <NavLink to="/user/login"><Button color="inherit">Login</Button></NavLink> }         
                    { !auth.isLoggedIn  && <NavLink to="/user/signup"><Button color="inherit">signup</Button></NavLink> }
                    { !auth.isLoggedIn  && <NavLink to="/admin/adminlogin"><Button color="inherit">Admin</Button></NavLink> }
                    { !auth.isLoggedIn && <NavLink to={"/books/" + null} ><Button color="inherit">All Books</Button></NavLink> }
                    <AdminLinks />
                    <UserNavLinks />
                    <SelectRole user={auth.userId} admin={auth.userId} />
                </Toolbar>
            </AppBar>
        </React.Fragment>
    )
}
