import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { Card, CardContent, Typography, CardActions, Button } from '@material-ui/core';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import ReactFlipCard from 'react-card-flip'
import './Card.css';


export default function BookCard(props) {

    const auth = useContext(AuthContext);
    const history = useHistory();
    let userData = JSON.parse(localStorage.getItem('userData'));
    let userBookList = userData.userBookList;

    const [isFlipped, setIsFlipped] = useState(false);

    const handleClick = (event) => {
        event.preventDefault();
        console.log('tere')
        setIsFlipped(!isFlipped);
    }

    const addToList = id => event => {

        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(responseData => {
            if(responseData) {
                const addedBookId = responseData.data.id;
                userBookList.push(addedBookId);
                localStorage.setItem('userData', JSON.stringify(userData))
                history.push('/' + auth.userId + '/booklist/');  

            }
        })
        .catch(error => {
            if (error.response) {
                console.log(error.response.data);
                console.log(error.response.status);
                console.log(error.response.headers);
              }
        });    
    }



    return(
        <ReactFlipCard>
        <div className="bookItem_root">
              <div className="imageDiv">
                  <img src={props.imageUrl}></img>
              </div>
              <button onClick={handleClick}>flip</button>
        </div>
        <div>
                <p>tere</p>
                    <button onClick={handleClick}>flip</button>



        </div>
        </ReactFlipCard>
    )
}
