import React from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button, TableFooter, TablePagination } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import FirstPageIcon from '@material-ui/icons/FirstPage';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import LastPageIcon from '@material-ui/icons/LastPage';
import PropTypes from 'prop-types';
import { makeStyles, useTheme } from '@material-ui/core/styles';



function TablePaginationActions(props) {
  const classes = useStyles1();
  const theme = useTheme();
  const { count, page, rowsPerPage, onChangePage } = props;

  const handleFirstPageButtonClick = (event) => {
    onChangePage(event, 0);
  };

  const handleBackButtonClick = (event) => {
    onChangePage(event, page - 1);
  };

  const handleNextButtonClick = (event) => {
    onChangePage(event, page + 1);
  };

  const handleLastPageButtonClick = (event) => {
    onChangePage(event, Math.max(0, Math.ceil(count / rowsPerPage) - 1));
  };

  return (
    <div className={classes.root}>
      <IconButton
        onClick={handleFirstPageButtonClick}
        disabled={page === 0}
        aria-label="first page"
      >
        {theme.direction === 'rtl' ? <LastPageIcon /> : <FirstPageIcon />}
      </IconButton>
      <IconButton onClick={handleBackButtonClick} disabled={page === 0} aria-label="previous page">
        {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
      </IconButton>
      <IconButton
        onClick={handleNextButtonClick}
        disabled={page >= Math.ceil(count / rowsPerPage) - 1}
        aria-label="next page"
      >
        {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
      </IconButton>
      <IconButton
        onClick={handleLastPageButtonClick}
        disabled={page >= Math.ceil(count / rowsPerPage) - 1}
        aria-label="last page"
      >
        {theme.direction === 'rtl' ? <FirstPageIcon /> : <LastPageIcon />}
      </IconButton>
    </div>
  );
}

TablePaginationActions.propTypes = {
  count: PropTypes.number.isRequired,
  onChangePage: PropTypes.func.isRequired,
  page: PropTypes.number.isRequired,
  rowsPerPage: PropTypes.number.isRequired,
};

export default function AdminTable({handleclickOpen, loadedBooks}) {


  const emptyRows = rowsPerPage - Math.min(rowsPerPage, loadedBooks.length - page * rowsPerPage);



    return(
        <TableContainer component={Paper}>
            <h2>Books</h2>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell align="left">Title </TableCell>
                <TableCell align="left">Author </TableCell>
                <TableCell align="left"> ImageURL </TableCell>
                <TableCell align="left"> Description</TableCell>
                <TableCell align="left"> Year</TableCell>
                <TableCell align="left"> Pages</TableCell>
                <TableCell align="left"className="actions">Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              { loadedBooks &&
              (rowsPerPage > 0
                ? loadedBooks.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                : loadedBooks
              ).map((row) => (
                <TableRow key={row.id}>
                  <TableCell  name="title" component="th" scope="row">{row.title}</TableCell>
                  <TableCell  name="author" align="right"><div> {row.author} </div></TableCell>
                  <TableCell  name="imageUrl" align="right">{row.imageUrl}</TableCell>
                  <TableCell  name="description" align="right" className="text2">{row.description}</TableCell>
                  <TableCell  name="year" align="right">{row.year}</TableCell>
                  <TableCell  name="pages" align="right">{row.pages}</TableCell>
                  <TableCell align="right">
                    <Button href={"/admin/edit/" + row.bookId} >EDIT</Button>
                    <Button color="secondary"  onClick={(e) => handleclickOpen(row.bookId)} >DELETE</Button>
                  </TableCell>
                </TableRow>
              )) }
                {emptyRows > 0 && (
            <TableRow style={{ height: 53 * emptyRows }}>
              <TableCell colSpan={6} />
            </TableRow>
          )}
            </TableBody>
            <TableFooter>
              <TableRow>
                <TablePagination
                rowsPerPageOptions={[5, 10, 25, { label: 'All', value: -1 }]}
                colSpan={3}
                count={loadedBooks.length}
                rowsPerPage={rowsPerPage}
                page={page}
                SelectProps={{
                  inputProps: { 'aria-label': 'rows per page' },
                  native: true,
                }}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
                ActionsComponent={TablePaginationActions}
                 />
              </TableRow>
            </TableFooter>
          </Table>
        </TableContainer>
    )
}
