import React, {useEffect, useState} from 'react';
import axios from 'axios';
import Container from '@material-ui/core/Container';
import { useParams } from 'react-router-dom';
import './SingleBookPage.css';
import { Grid } from '@material-ui/core';


export default function SingleBookPage() {

    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;

    useEffect( () => {

        const fetchData = async () => {
            try {
                const responseData = await axios.get('http://localhost:8000/api/bookBlog/book/' + bookId);
                const books = responseData.data.book;
                setLoadedBook(books);
            } catch(err) {
                console.log(err.message)
            }
        }
        fetchData();

 }, [bookId, setLoadedBook]);

 return(
    <Container maxWidth="sm">
        {loadedBook && 
        <React.Fragment>
             <Grid container spacing={3}>
            <div className="single_book_flex">
            <div className="imgDiv">
                <img src={loadedBook.imageUrl} alt={loadedBook.title} />
            </div>
            <div>
                <h2> {loadedBook.title} </h2>
                    <h3> by {loadedBook.author} </h3>
                        <article >
                            {loadedBook.description}
                        </article>

            </div>



            </div>
            </Grid>        
        </React.Fragment>
        }
    </Container>
)
}
