import React, { useState, useEffect, useRef, useMemo } from 'react';
import axios from 'axios';
import SearchResultsPage from '../searchResultsPage/SearchResultsPage';
import { Redirect } from 'react-router-dom';
import { Button } from '@material-ui/core';

const Search = React.memo(props => {

    const [searchText, setSearchText] = React.useState('');
    const [searchResult, setSearchResult] = React.useState('');
    const [loadedBooks, setLoadedBooks] = useState([]);


    const submit = () => {
        let url = 'http://localhost:8000/api/bookBlog/books?q=' + encodeURI(searchText) + '&json=1';

           axios.get(url)
           .then( response => {
               const books = response.data.books;
               setLoadedBooks(books)
           })
           .catch(err => console.log(err))       
    }

    const handleText = (event) => {
        setSearchText(event.target.value);
      };


      React.useMemo(() => {
        const results = loadedBooks.filter(book =>
          book.title.toLowerCase().includes(searchText)
        );
        setSearchResult(results);
      }, [searchText, loadedBooks]);





  return (
      <React.Fragment>
      <form onSubmit={submit}>
        <input onChange={handleText}/>
        <Button type="submit" color="primary">Find</Button>
        </form>

        {/*searchResult > 0 &&
            <Redirect to={{
              pathname: '/results',
              state: { results: searchResult }
            }}/>
        */}

        </React.Fragment>

  );



    })
export default Search;
