import React from 'react'
import { useState, useEffect } from "react";
import { onChangeState, onChangeOrientation, HTMLFlipBook  } from 'react-pageflip'

const PageCover = React.forwardRef((props, ref) => {
    return (
      <div className="page page-cover" ref={ref} data-density="hard">
        <div className="page-content">
          <h2>{props.children}</h2>
        </div>
      </div>
    );
  });

  const Page = React.forwardRef((props, ref) => {
    return (
      <div className="page" ref={ref}>
        <div className="page-content">
          <h2 className="page-header">Page header - {props.number}</h2>
          <div className="page-image"></div>
          <div className="page-text">{props.children}</div>
          <div className="page-footer">{props.number + 1}</div>
        </div>
      </div>
    );
  });

export default function DemoBook()  {

    const [state, setState] = useState({page: 0, totalPage: 0})


    const nextButtonClick = () => {
      this.flipBook.getPageFlip().flipNext();
    };

    const prevButtonClick = () => {
      this.flipBook.getPageFlip().flipPrev();
    };

    const onPage = (e) => {
      setState({page: e.data});
    }

      useEffect(() => {
          setState({totalPage: this.flipBook.getPageFlip().getPageCount()})

      }, [])



      return (
        <div>
          <HTMLFlipBook
            width={550}
            height={733}
            size="stretch"
            minWidth={315}
            maxWidth={1000}
            minHeight={400}
            maxHeight={1533}
            maxShadowOpacity={0.5}
            showCover={true}
            mobileScrollSupport={true}
            onFlip={onPage}
            onChangeOrientation={onChangeOrientation}
            onChangeState={onChangeState}
            className="demo-book"
            ref={(el) => (this.flipBook = el)}
          >

            <PageCover>BOOK TITLE</PageCover>
            <Page number={1}>Lorem ipsum...</Page>
            <Page number={2}>Lorem ipsum...</Page>
            /*...*/
            <PageCover>THE END</PageCover>

          </HTMLFlipBook>

          <div className="container">
            <div>

              <button type="button" onClick={prevButtonClick}>
                Previous page
              </button>

              [<span>{state.page}</span> of
               <span>{state.totalPage}</span>]

              <button type="button" onClick={nextButtonClick}>
                Next page
              </button>

            </div>

          </div>
        </div>
      );
    }
