import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BookCard from '../../shared/Card/Card';
import './GeneralBooklist.css';
import { Row, Col } from 'react-simple-flex-grid';
import "react-simple-flex-grid/lib/main.css";

export default function GeneralBookList() {

    const [loadedBook, setLoadedBook] = useState([]);

    useEffect( () => {

           axios.get('http://localhost:8000/api/bookBlog/books/')
           .then( response => {
               const books = response.data.books;
               setLoadedBook(books)
           })
           .catch(err => console.log(err))       
    }, []);


    return(

           <div>
        <h1> All Books</h1>

        <Row>

        {loadedBook &&  loadedBook.map(book => {
            return  <Col span={4} order={4}> <BookCard
             key = {book.id}
             id = {book.id}
             title={book.title}
             author={book.author}
             pages={book.pages}

             description={book.description}
             imageUrl= {book.imageUrl}
             year={book.year}
             /></Col>})
        }

        </Row>

        </div>   
    )
}
