import React, { useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { Card, CardMedia, CardContent, Typography, CardActions, Button, Grid, makeStyles } from '@material-ui/core';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';
import './Card.css';
import { useTheme } from "@material-ui/styles";
import useMediaQuery from "@material-ui/core/useMediaQuery";

const useStyles = makeStyles(theme => ({
    root: {
      maxWidth: 345,
      [theme.breakpoints.down("md")] : {
      maxWidth: 200
      }
    },
    media: {
      height: 140
    }
  }));

export default function BookCard(props) {

    const classes = useStyles();
    const theme = useTheme();
    const matches = useMediaQuery(theme.breakpoints.up("sm"));



    const auth = useContext(AuthContext);
    const history = useHistory();

    const addToList = id => event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(response => {
            console.log(response.data);
            history.push('/' + auth.userId + '/booklist/');  
        })
        .catch(err => console.log(err));    
    }

    console.log(props.imageUrl)
    return(
        <Grid item xs={12} sm container>
        <Card className="bookItem_root">
              <CardMedia
                    className="cover"
                    image={props.imageUrl}
                    title={props.title}
                />
            <div className="bookItem_details">
                <CardContent className="content">
                    <Typography component="h5" variant="h5">
                       <Link to={/book/ + props.id }> {props.title} ({props.year}) </Link>
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {props.author}
                    </Typography>
                    <hr />
                    <Typography align="justify" className="description" variant="body2" component="p">
                        {props.description}
                    </Typography>
                    <br />
                    <div>
                      <footer>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {props.pages} </strong>  
                    </Typography>
                    <div className="spacer"></div>
                    <CardActions className="cardActions">
                       {auth.isLoggedIn && auth.role === 1 &&
                        <Button
                          type="submit"
                          onClick={addToList(props.id)}
                          variant="outlined"
                          color="primary"
                          size="small">add to bookList
                        </Button>}
                    </CardActions>
                    </footer>
                    </div>
                    </CardContent>
                    </div>


        </Card>
        </Grid>
    )
}
