import React, { useContext } from 'react';
import axios from 'axios';
import { useHistory, Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import './BookItem.css'
import { Button } from '@material-ui/core';
import { AuthContext } from '../../context/AuthContext';

const useStyles = makeStyles((theme) => ({


    content: {
      flex: '1 0 auto',
    },


    description: {
        margin: '10px'
    }

  }));


export default function BookItem({id, title, description, author, year, imageUrl, pages}) {

    const classes = useStyles();
    const history = useHistory();
    const auth = useContext(AuthContext)


    const addToList = id => event => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/bookBlog/booklist/' + auth.userId, {
           id: id,
           userId: auth.userId
        })
        .then(response => {
            console.log(response.data);
            history.push('/booklist/' + auth.userId);  
        })
        .catch(err => console.log(err));    
    }

    return(
        <Card className="bookItem_root">
              <CardMedia
                    className="cover"
                    image={imageUrl}
                    title={title}
                />
            <div className="bookItem_details">
                <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                       <Link to={/book/ + id }> {title} ({year}) </Link>
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {author}
                    </Typography>
                    <hr />
                    <Typography align="justify" className={classes.description} variant="body2" component="p">
                        {description}
                    </Typography>
                    <br />
                    <div>
                      <footer>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {pages} </strong>  
                    </Typography>
                    <div className="spacer"></div>
                    <CardActions className="cardActions">
                       {auth.isLoggedIn && auth.role === 1 &&
                        <Button
                          type="submit"
                          onClick={addToList(id)}
                          variant="outlined"
                          color="primary"
                          size="small">add to bookList
                        </Button>}
                    </CardActions>
                    </footer>
                    </div>
                    </CardContent>
                    </div>


        </Card>
    )
}
