import React from 'react';
import { Button, Card, makeStyles, CardContent, CardActionArea, CardActions, CardMedia } from '@material-ui/core';
import DeleteUSerBookDialog from '../DeleteModel/Deletemodel';
import './UserBooklistItem.css';

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
      backgroundSize: "contain"
    },
  });


export default function UserBooklistItem({
     id,
     imageUrl,
     title,
     author,
     open,
     bookIndex,
     handleclickOpen,
     handleClose,
     handleDeleteUserBook
    }) {    
    const classes = useStyles();

    return(
            <div className="container">
                <div className="card-wrap">
                <Card className={classes.root}>
                    <CardActionArea>
                    <CardMedia className={classes.media}
                        image={imageUrl}
                         title="Contemplative Reptile"
                     />
                    <CardContent >
                        <h3 className="card-title"> {title} </h3>
                        <h4 className="card-author"> {author} </h4>
                        <CardActionArea>
                            <CardActions>
                                <Button
                                 className="removeBookButton"
                                 onClick={(e) => handleclickOpen(id)}
                                 color="primary">
                                     remove book
                                </Button>
                            </CardActions>
                        </CardActionArea>
                        </CardContent>
                    </CardActionArea>
                </Card>
                        <DeleteUSerBookDialog
                           open={open}
                           handleClose={handleClose}
                           handleclickOpen={handleclickOpen}
                           handleDeleteUserBook={handleDeleteUserBook}
                           bookid={bookIndex}/>

                </div>
            </div>
    )

}
