import React, { useContext, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SearchInput from '../searchInput/SearchInput';
import { AuthContext } from '../../context/AuthContext';
import SelectRole from './selectRole/SelectRole';
import AdminLinks from './Navlinks/AdminNavLinks';
import UserNavLinks from './Navlinks/UserNavLinks';
import GeneralNavLinks from './Navlinks/GeneralNavLinks';
import './Header.css';
import { Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  }
}))

export default function AppHeader()  {

  const {logout, isLoggedIn} = useContext(AuthContext)



    const classes = useStyles();
    return(
        <React.Fragment>
            <AppBar position="static">
                <Toolbar>
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton>
                    <h2>BookReader</h2>
                    <SearchInput />
                    <div className="spacer"></div>
                    <GeneralNavLinks />

                    { isLoggedIn && <Button logout={logout} href="/user/login" color="inherit">Logout</Button>}

                </Toolbar>
            </AppBar>
        </React.Fragment>
    )
}
