import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import './BookItem.css'
import { Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      marginBottom: '30px'
    },
    details: {
      display: 'flex',
      flexDirection: 'column',
    },
    content: {
      flex: '1 0 auto',
    },
    cover: {
      width: 151,
    },

    description: {
        margin: '10px'
    }

  }));


export default function BookItem({title, description, author, year, imageUrl}) {

    const classes = useStyles();
    const theme = useTheme();

    return(
        <Card className={classes.root}>
              <CardMedia
                    className={classes.cover}
                    image={imageUrl}
                    title={title}
                />
            <div className={classes.details}>
                <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                        {title} ({year})
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {author}
                    </Typography>
                    <hr />
                    <Typography className={classes.description} variant="body2" component="p">
                        "Kevade: pildikesi koolipõlvest" on Oskar Lutsu jutustus või lühiromaan,
                        mille esimene väljaanne ilmus kahes osas aastatel 1912 ja 1913.
                        Lutsu koolipõlvemälestustel põhinev raamat on üks Eesti kirjanduse tuntumaid teoseid,
                        mis on ilmunud 21 trükis ja tõlgitud 13 keelde.
                        Jutustuse põhjal valmis 1970. aastal Arvo Kruusemendi mängufilm "Kevade", 
                        samuti on teose instseneeringuid korduvalt lavastatud Eesti teatrites
                    </Typography>
                    <br />
                    <div>
                    <Typography variant="subtitle2" color="textSecondary">
                        Pages: <strong>344</strong>   Type: HardCover 
                    </Typography>
                    <CardActions>
        <Button size="small">Learn More</Button>
      </CardActions>
      </div>
                </CardContent>
                </div>


        </Card>
    )
}
