import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import SearchResultsPage from '../searchResultsPage/SearchResultsPAge';

const Search = React.memo(props => {

  const [enteredFilter, setEnteredFilter] = useState('');
  const inputRef = useRef();

  const [loadedBooks, setLoadedBooks] = useState([]);

    useEffect( () => {

           axios.get('http://localhost:8000/api/bookBlog/books/')
           .then( response => {
               const books = response.data.books;
               setLoadedBooks(books)
           })
           .catch(err => console.log(err))       
    }, []);


  useEffect(() => {
    const timer = setTimeout(() => {
      if (enteredFilter === inputRef.current.value) {
        const query =
          enteredFilter.length === 0
            ? ''
            : `?orderBy="title"&equalTo="${enteredFilter}"`;
        fetch(
          'http://localhost:8000/api/bookBlog/books/' + query
        )
          .then(response => response.json())
          .then(responseData => {
            const loadedBooks = [];
            for (const key in responseData) {
              loadedBooks.push({
                id: key,
                title: responseData[key].title,
                author: responseData[key].author
              });
            }
            setLoadedBooks(loadedBooks);
          });
      }
    }, 500);
    return () => {
      clearTimeout(timer);
    };
  }, [enteredFilter, loadedBooks, inputRef]);

  return (
      <React.Fragment>
    <section className="search">
        <div className="search-input">
          <input
            ref={inputRef}
            type="text"
            value={enteredFilter}
            onChange={event => setEnteredFilter(event.target.value)}
          />
        </div>
    </section>
                <SearchResultsPage loadedBooks={loadedBooks} />
                </React.Fragment>

  );
});


export default Search;
