import React, { useState, useEffect } from 'react';
import { TextField, Button } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import Axios from 'axios';


export default function EditBook() {

    const [loadedBook, setLoadedBook] = useState([]);

    const [updatedBook, setUpdatedBook] = useState();



    const bookId = useParams().bookId;

    useEffect(() => {

        Axios.get(`http://localhost:8000/api/bookBlog/book/${bookId}`)
        .then(response => {
          const book = response.data.book;

      setLoadedBook(book)   
    })
    .catch(err => console.log(err))
    }, [bookId])

    const handleChange = (event) => {
        event.persist();
        setUpdatedBook(updatedBook => ({
          ...updatedBook,
          [event.target.name]: event.target.value
      }));
     }

    const submitEditedBook = (event) => {
        event.preventDefault();
        const newBook = {
            title: updatedBook.title,
            author: updatedBook.author,
            imageUrl: updatedBook.imageUrl,
            year: updatedBook.year,
            pages: updatedBook.pages,
            description: updatedBook.description
          } 
          setLoadedBook(newBook);
        Axios.patch(`http://localhost:8000/api/admin/books/edit/${bookId}`, newBook)
        .then(response => {
            console.log(response)
        })
        .catch(err => console.log(err))
    }

    return(
        <form  className="bookForm" onSubmit={submitEditedBook} >
        <h2>Edit Book</h2>

        <TextField 
        id="input"
        type="text"
        name="title"
        onChange={handleChange}
        value={loadedBook.title}
        required
        />
        <TextField
        id="input"
        type="text"
        name="author"
        value={loadedBook.author}
        required
         />
        <TextField
        id="input"
        type="text"
        name="imageUrl"
        value={loadedBook.imageUrl}
        required
         />
        <TextField
        id="input"
        type="number"
        name="year"
        value={loadedBook.year}

        required
        />
        <TextField
        id="input"
        type="number"
        name="pages"
        value={loadedBook.pages}

        required
         />
        <TextField
        id="textarea"
        type="text"
        name="description"
        value={loadedBook.description}
        multiline
        rows={3}
        rowsMax={3}
        />

      <Button
        className="editBookButton"
        color="primary"
        size="large"
        variant="contained"
        type="submit" >
             EDIT BOOK
        </Button> 

    </form>
    )
}
