import React, { useContext } from 'react';
import { AuthContext } from '../../../context/AuthContext';
import { NavLink } from 'react-router-dom';
import { Button } from '@material-ui/core';


export default function GeneralNavLinks() {

   const {isLoggedIn, role, userId, logout} = useContext(AuthContext);

    return (
        <React.Fragment>
             { isLoggedIn && role === 1 &&
              <NavLink to={ '/' + userId + "/booklist" } >
                  <Button color="inherit">
                      Your Books
                  </Button>
             </NavLink> }

          { role === 3 || 2 && isLoggedIn &&
             <NavLink to={"/admin/books"} >
                <Button color="inherit">
                    All Books
                </Button>
            </NavLink> }

          { role === 3 && isLoggedIn &&
             <NavLink to={"/admin/adminpanel"} >
                <Button color="inherit">
                    AdminPanel
                </Button>
            </NavLink> }
        { role === 3 && 
            <NavLink to={"/books/new"} >
                <Button color="inherit">
                    Add Book
                </Button>
            </NavLink> }
            { !isLoggedIn &&  <NavLink to="/user/login"><Button color="inherit">Login</Button></NavLink> }         
            { !isLoggedIn  && <NavLink to="/user/signup"><Button color="inherit">signup</Button></NavLink> }
            { !isLoggedIn  && <NavLink to="/admin/adminlogin"><Button color="inherit">Admin</Button></NavLink> }
            { !isLoggedIn && <NavLink to={"/books"} ><Button color="inherit">All Books</Button></NavLink> }
            { isLoggedIn && <Button onClick={logout} href="/user/login" color="inherit">Logout</Button>}

        </React.Fragment>
    )
}
