import React, { useState, useCallback, useContext } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { withRouter, useHistory, Link } from 'react-router-dom';
import './Login.css'
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext';
import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';

const LoginForm = () => {

    const [user, setUser] = useState({
       email: "",
       password: ""
     });

     const [isLoading, setIsloading] = useState(false);


     const history = useHistory();
     const auth = useContext(AuthContext)

    const handleChange = (event) => {
       event.persist();
       setUser(user => ({
         ...user,
         [event.target.name]: event.target.value
     }));
    }    

    const handleSubmit =  useCallback( async (event) => {
       event.preventDefault();
       setIsloading(true);
      try {
        const responseData = await axios.post('http://localhost:8000/api/user/login', user)
        if(responseData) {
          console.log(responseData)
          const userId = responseData.data.id
          //const token = responseData.data.accessToken
          const role = responseData.data.roles
          const booklist = responseData.data.booklist
          auth.login(userId, role[0], booklist)
          history.push('/' + userId +'/booklist'  )
        }

      }
      catch(err) {
        console.log(err.message)
      }

   }, [auth, history, user]);


    return(
       <React.Fragment>
           {isLoading && <CircularIndeterminate size="7rem" color="primary" />}
        <Container> 
        <form  className="LoginForm" onSubmit={handleSubmit} >
            <h1>Login</h1>

            <TextField 
             id="input"
            label="E-mail"
            type="email"
            name="email"
            required
            onChange={handleChange} />
            <TextField
             id="input"
            label="Password"
            type="password"
            name="password"
            required
            onChange={handleChange} />

          <Button
            className="loginButton"
            color="primary"
            variant="contained"
            type="submit" >
                 LOG IN
            </Button> 
            <Link to="/admin/adminlogin">Admin?</Link>
        </form>

        </Container>
        </React.Fragment>
    )

}

export default withRouter(LoginForm);
