import React, { useState } from 'react';
import FlipCard from 'react-flipcard';


export default function FlipedCard(props) {



    return(
        <div>
        {/* Default behavior is horizontal flip on hover, or focus */}
        <FlipCard>
          {props.children}
          <div>
            <div>Front</div>
            <div><small>(horizontal flip)</small></div>
          </div>
          {props.children}
          <div>Back</div>
        </FlipCard>
        </div>
    )
}
