import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import './BookItem.css'
import { Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      marginBottom: '30px',
      position: 'relative'
    },
    details: {
      display: 'flex',
      flexDirection: 'column',
    },
    content: {
      flex: '1 0 auto',
    },
    cover: {
      width: 151,
    },

    description: {
        margin: '10px'
    }

  }));


export default function BookItem({id, title, description, author, year, imageUrl, pages, addToList}) {

    const classes = useStyles();
    const theme = useTheme();

    return(
        <Card className={classes.root}>
              <CardMedia
                    className={classes.cover}
                    image={imageUrl}
                    title={title}
                />
            <div className={classes.details}>
                <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                        {title} ({year})
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        by {author}
                    </Typography>
                    <hr />
                    <Typography align="justify" gutterBottom="true" className={classes.description} variant="body2" component="p">
                        {description}
                    </Typography>
                    <br />
                    <div>
                    <Typography className="pages" variant="subtitle2" color="textSecondary">
                        Pages: <strong> {pages} </strong>  
                    </Typography>
                    <CardActions className="cardActions">
                        <Button type="submit" onClick={addToList(id)} className="bookItemButton" variant="outlined" color="primary" size="small">add to bookList</Button>
                    </CardActions>
                    </div>
                    </CardContent>
                    </div>


        </Card>
    )
}
