import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import './Header.css';
import SearchInput from '../searchInput/SearchInput';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  }
}))

export default function AppHeader() {
  const classes = useStyles();

  const auth = useContext(AuthContext)


  return (
    <div className="header">
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <h2>BookReader</h2>
          <SearchInput />
          <div className="spacer"></div>
          { !auth.isLoggedIn &&       
         <NavLink to="/user/login"><Button color="inherit">Login</Button></NavLink> 
          }         
         { !auth.isLoggedIn  && <NavLink to="/user/signup"><Button color="inherit">signup</Button></NavLink> }
         { !auth.isLoggedIn  && <NavLink to="/admin/adminlogin"><Button color="inherit">Admin</Button></NavLink> }

         { auth.isLoggedIn && <NavLink to={"/booklist/" + auth.userId} ><Button color="inherit">Booklist</Button></NavLink> }
         { auth.role === 3 && auth.isLoggedIn && <NavLink to={"/admin/adminpanel/"} ><Button color="inherit">AdminPanel</Button></NavLink> }
         { auth.role === 3  && <NavLink to={"/books/new"} ><Button color="inherit">Add Book</Button></NavLink> }

         { <NavLink to={"/books/" + auth.userId} ><Button color="inherit">Books</Button></NavLink> }
         { auth.isLoggedIn && <Button href="/user/login" onClick={auth.logout} >logout</Button>}
        </Toolbar>
      </AppBar>
    </div>
  );
}
