import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './SingleBookPage.css';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { Container } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    root: {
      height: '100vh',
    },
    image: {
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    paper: {
      margin: theme.spacing(8, 4),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },

  }));


export default function SingleBookPage() {

    const classes = useStyles();

    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;

    useEffect( () => {

        const fetchData = async () => {
            try {
                const responseData = await axios.get('http://localhost:8000/api/bookBlog/book/' + bookId);
                const books = responseData.data.book;
                setLoadedBook(books);
            } catch(err) {
                console.log(err.message)
            }
        }
        fetchData();

 }, [bookId, setLoadedBook]);

 return(
     <React.Fragment>
     {loadedBook && 
     <Container className="book_container" maxWidth="lm">
    <Grid container component="main" className={classes.root}>
    <CssBaseline />
    <Grid item xs={12} sm={8} md={5} className={classes.image}  style={{ backgroundImage: `url(${loadedBook.imageUrl})` }}  />

    <Grid item xs={12} sm={8} md={5} component={Paper}  square>

    <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6}  square>




      </Grid>
      </Grid>
        </Grid>
  </Container>

     }
     </React.Fragment>
);

}
