import React, { useState, useEffect } from 'react';
import { TextField, Button } from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';


export default function EditBook() {

    const [loadedBook, setLoadedBook] = useState([]);
    const bookId = useParams().bookId;
    const history = useHistory()

    useEffect(() => {

        axios.get(`http://localhost:8000/api/bookBlog/book/${bookId}`)
        .then(response => {
          const book = response.data.book;
          setLoadedBook(book)   
        })
        .catch(err => console.log(err))
        }, [bookId])

        const handleChange = (event) => {
            event.persist();
            setLoadedBook(loadedBook => ({
                ...loadedBook,
                [event.target.name]: event.target.value
            }));
        }

    const submitEditedBook = (event) => {

        event.preventDefault();
        const updatedBook = {
            title: loadedBook.title,
            author: loadedBook.author,
            imageUrl: loadedBook.imageUrl,
            year: loadedBook.year,
            pages: loadedBook.pages,
            description: loadedBook.description
          } 
          setLoadedBook(updatedBook);
        axios.patch(`http://localhost:8000/api/admin/books/edit/${bookId}`, updatedBook)
        .then(response => {
            return <Alert severity="success">Book is Edited</Alert>
            history.push('/admin/adminpanel')

        })
        .catch(err => console.log(err))
    }

    return(
        <form  className="bookForm" onSubmit={submitEditedBook} >
        <h2>Edit Book</h2>

        <TextField 
        id="input"
        type="text"
        name="title"
        onChange={handleChange}
        value={loadedBook.title || ''}
        required
        />
        <TextField
        id="input"
        type="text"
        name="author"
        onChange={handleChange}
        value={loadedBook.author || ''}
        required
         />
        <TextField
        id="input"
        type="text"
        name="imageUrl"
        onChange={handleChange}
        value={loadedBook.imageUrl || ''}
        required
         />
        <TextField
        id="input"
        type="number"
        name="year"
        onChange={handleChange}
        value={loadedBook.year || ''}
        required
        />
        <TextField
        id="input"
        type="number"
        name="pages"
        value={loadedBook.pages || ''}
        onChange={handleChange}
        required
         />
        <TextField
        id="textarea"
        type="text"
        name="description"
        onChange={handleChange}
        value={loadedBook.description || ''}
        multiline
        rows={3}
        rowsMax={3}
        />

      <Button
        className="editBookButton"
        color="primary"
        size="large"
        variant="contained"
        type="submit" >
             EDIT BOOK
        </Button> 
    </form>
    )
}
