import React, { useContext, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SearchInput from '../searchInput/SearchInput';
import { AuthContext } from '../../context/AuthContext';
import SelectRole from './selectRole/SelectRole';
import AdminLinks from './Navlinks/AdminNavLinks';
import UserNavLinks from './Navlinks/UserNavLinks';
import GeneralNavLinks from './Navlinks/GeneralNavLinks';
import './Header.css';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  }
}))

export default function AppHeader()  {

  const roles = [{id: 1, role: 'user'}, {id:2, role: 'admin'}]
  const [role, setRole] = useState('');

  const handleChange = (event) => {
    setRole(event.target.value);
  };



    const classes = useStyles();
    return(
        <React.Fragment>
            <AppBar position="static">
                <Toolbar>
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton>
                    <h2>BookReader</h2>
                    <SearchInput />
                    <div className="spacer"></div>
                    {role=== 'user' ?
                    <AdminLinks />
    : <UserNavLinks /> }
                    <SelectRole role={role} handleChange={handleChange} roles={roles}  />
                </Toolbar>
            </AppBar>
        </React.Fragment>
    )
}
